import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { translations } from '../translations/translations';

const API_BASE = 'http://localhost:5000';

function VoiceAssistant({ language }) {
  const [isListening, setIsListening] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [inputMessage, setInputMessage] = useState('');
  const [recognition, setRecognition] = useState(null);
  const t = translations[language];

  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recog = new SpeechRecognition();
      recog.continuous = false;
      recog.interimResults = false;

      const langCode = language === 'hi' ? 'hi-IN' : language === 'kn' ? 'kn-IN' : 'en-IN';
      recog.lang = langCode;

      recog.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        handleSendMessage(transcript);
      };

      recog.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recog.onend = () => {
        setIsListening(false);
      };

      setRecognition(recog);
    }
  }, [language]);

  const startListening = () => {
    if (recognition) {
      setIsListening(true);
      setShowChat(true);
      recognition.start();
    } else {
      alert('Speech recognition is not supported in your browser. Please use Chrome or Edge.');
    }
  };

  const handleSendMessage = async (text) => {
    if (!text.trim()) return;

    const userMessage = { sender: 'user', text: text };
    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');

    const lower = text.toLowerCase();

    // Simple intent detection for weather-related queries (expandable)
    const weatherKeywords = ['weather', 'temperature', 'forecast', 'rain', 'humidity', 'mausam', 'à¤®à¥Œà¤¸à¤®'];
    const isWeather = weatherKeywords.some(k => lower.includes(k));

    // Try to extract location from phrases like "weather in <city>"
    let location = '';
    const locMatch = lower.match(/in ([a-z\s]+)/i);
    if (locMatch && locMatch[1]) {
      location = locMatch[1].trim();
    }

    if (isWeather) {
      try {
        // Call backend weather endpoint (defaults to Bangalore if no location)
        const params = location ? { params: { location } } : {};
        const res = await axios.get(`${API_BASE}/weather`, params);
        const info = res.data;

        // Build a readable summary
        let botText = `Weather for ${info.location}: ${info.description}. Temperature ${info.temperature}Â°C (feels like ${info.feels_like}Â°C). Humidity ${info.humidity}%. Wind ${info.wind_speed} m/s.`;
        if (info.alerts && info.alerts.length) {
          botText += '\nAlerts:' + info.alerts.map(a => `\n- ${a.message}`).join('');
        }

        if (info.forecast && info.forecast.length) {
          botText += '\n\nUpcoming forecast (next few entries):';
          info.forecast.slice(0, 3).forEach(f => {
            botText += `\n${f.time}: ${f.temp}Â°C, ${f.description}`;
          });
        }

        const botMessage = { sender: 'bot', text: botText };
        setMessages(prev => [...prev, botMessage]);
        speakText(botText);
      } catch (err) {
        console.error('Weather fetch error:', err);
        const errorMsg = { sender: 'bot', text: 'Sorry, I could not fetch the weather right now.' };
        setMessages(prev => [...prev, errorMsg]);
      }

      return;
    }

    // Market prices intent
    const marketKeywords = ['price', 'prices', 'rate', 'rates', 'market', 'cost'];
    const isMarket = marketKeywords.some(k => lower.includes(k));

    if (isMarket) {
      try {
        // Try to extract crop name from 'price of <crop>' or 'prices for <crop>' patterns
        let crop = '';
        const m = lower.match(/(?:price|prices|rate|rates|cost) (?:of |for )?([a-z\s]+)/i) || lower.match(/for ([a-z\s]+)\b/i);
        if (m && m[1]) crop = m[1].trim();

        const params = crop ? { params: { crop } } : {};
        const res = await axios.get(`${API_BASE}/market`, params);
        const data = res.data;

        let botText = '';
        if (data.prices && data.prices.length) {
          botText = `Market prices${crop ? ' for ' + crop : ''}:`;
          data.prices.slice(0, 5).forEach(p => {
            const change = p.change >= 0 ? `+${p.change}` : p.change;
            botText += `\n- ${p.crop} at ${p.location}: â‚¹${p.current_price} (change ${change})`;
          });
        } else {
          botText = 'No market price data found for that query.';
        }

        const botMessage = { sender: 'bot', text: botText };
        setMessages(prev => [...prev, botMessage]);
        speakText(botText);
      } catch (err) {
        console.error('Market fetch error:', err);
        const errorMsg = { sender: 'bot', text: 'Sorry, I could not fetch market prices right now.' };
        setMessages(prev => [...prev, errorMsg]);
      }

      return;
    }

    // Crop recommendation intent
    const cropKeywords = ['crop', 'recommend', 'suggest', 'recommendation', 'suggestion', 'à¤•à¥ƒà¤·à¤¿', 'à¤«à¤¸à¤²', 'à¤¸à¤¿à¤«à¤¾à¤°à¤¿à¤¶'];
    const isCrop = cropKeywords.some(k => lower.includes(k));

    if (isCrop) {
      try {
        // Try to extract soil and season if present
        const soilMatch = lower.match(/(clay|sandy|loamy|black)/i);
        const seasonMatch = lower.match(/(kharif|rabi|summer|winter|monsoon|all)/i);
        const soil = soilMatch ? soilMatch[1].toLowerCase() : '';
        const season = seasonMatch ? seasonMatch[1].toLowerCase() : '';

        const params = { params: {} };
        if (soil) params.params.soil = soil;
        if (season) params.params.season = season;

        // If neither soil nor season found, call without params to get general recommendations
        const callParams = (soil || season) ? params : {};
        const res = await axios.get(`${API_BASE}/crop`, callParams);
        const data = res.data;

        let botText = '';
        if (data.recommendations && data.recommendations.length) {
          botText = 'Recommended crops:';
          data.recommendations.slice(0, 6).forEach(r => {
            botText += `\n- ${r.name} â€” ${r.duration || ''} ${r.yield ? ` â€” yield: ${r.yield}` : ''}`;
          });
        } else {
          botText = 'No crop recommendations found for the provided criteria.';
        }

        const botMessage = { sender: 'bot', text: botText };
        setMessages(prev => [...prev, botMessage]);
        speakText(botText);
      } catch (err) {
        console.error('Crop fetch error:', err);
        const errorMsg = { sender: 'bot', text: 'Sorry, I could not fetch crop recommendations right now.' };
        setMessages(prev => [...prev, errorMsg]);
      }

      return;
    }

    // Fallback to existing chatbot endpoint for other intents
    try {
      const response = await axios.post(`${API_BASE}/chatbot`, {
        message: text,
        language: language
      });

      const botMessage = { sender: 'bot', text: response.data.response };
      setMessages(prev => [...prev, botMessage]);

      speakText(response.data.response);
    } catch (error) {
      console.error('Error with chatbot:', error);
      const errorMsg = { sender: 'bot', text: 'Sorry, I encountered an error. Please try again.' };
      setMessages(prev => [...prev, errorMsg]);
    }
  };

  const speakText = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = language === 'hi' ? 'hi-IN' : language === 'kn' ? 'kn-IN' : 'en-IN';
      utterance.rate = 0.9;
      window.speechSynthesis.speak(utterance);
    }
  };

  return (
    <>
      <button
        className={`voice-btn ${isListening ? 'listening' : ''}`}
        onClick={isListening ? null : startListening}
        title={t.voiceAssistant}
      >
        ðŸŽ¤
      </button>

      {showChat && (
        <div style={{
          position: 'fixed',
          bottom: '100px',
          right: '30px',
          width: '350px',
          maxHeight: '500px',
          background: 'rgba(255, 255, 255, 0.2)',
          backdropFilter: 'blur(10px)',
          borderRadius: '10px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
          display: 'flex',
          flexDirection: 'column',
          zIndex: 1000
        }}>
          <div style={{
            background: '#3f5945',
            color: 'white',
            padding: '15px',
            borderTopLeftRadius: '10px',
            borderTopRightRadius: '10px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <h3 style={{ margin: 0 }}>ðŸ¤– {t.aiAssistant}</h3>
            <button
              onClick={() => setShowChat(false)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                fontSize: '20px',
                cursor: 'pointer'
              }}
            >
              âœ•
            </button>
          </div>

          <div className="chat-container" style={{ flex: 1, padding: '15px', overflowY: 'auto' }}>
            {messages.length === 0 && (
              <div style={{ textAlign: 'center', color: '#222', padding: '20px' }}>
                <p>ðŸ‘‹ {t.hiMessage}</p>
                <p>{t.askAbout}</p>
                <ul style={{ textAlign: 'left', marginTop: '10px' }}>
                  <li>{t.weatherUpdates}</li>
                  <li>{t.cropRecommendations}</li>
                  <li>{t.marketPricesVoice}</li>
                  <li>{t.govSchemesVoice}</li>
                  <li>{t.diseaseDetectionVoice}</li>
                </ul>
              </div>
            )}
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`message ${msg.sender}`}
                style={{
                  marginBottom: '10px',
                  padding: '10px',
                  borderRadius: '8px',
                  maxWidth: '80%',
                  marginLeft: msg.sender === 'user' ? 'auto' : '0',
                  background: msg.sender === 'user' ? '#3b4f1b' : 'rgba(255, 255, 255, 0.2)',
                  backdropFilter: msg.sender === 'bot' ? 'blur(10px)' : 'none',
                  color: msg.sender === 'user' ? 'white' : '#222'
                }}
              >
                {msg.text}
              </div>
            ))}
          </div>

          <div style={{ padding: '15px', borderTop: '1px solid #e0e0e0' }}>
            <div style={{ display: 'flex', gap: '10px' }}>
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage(inputMessage)}
                style={{
                  flex: 1,
                  padding: '10px',
                  border: '2px solid #3b4f1b',
                  borderRadius: '5px',
                  fontSize: '14px',
                  background: 'rgba(255, 255, 255, 0.2)',
                  backdropFilter: 'blur(10px)',
                  color: '#222'
                }}
              />
              <button
                onClick={() => handleSendMessage(inputMessage)}
                className="btn"
                style={{ padding: '10px 20px', margin: 0 }}
              >
                {t.send}
              </button>
              <button
                onClick={startListening}
                className="btn"
                style={{
                  padding: '10px 15px',
                  margin: 0,
                  background: isListening ? '#dc3545' : '#3b4f1b'
                }}
                disabled={isListening}
              >
                ðŸŽ¤
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default VoiceAssistant;
