import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { translations } from '../translations/translations';

const API_BASE = 'http://localhost:5000';

function Dashboard({ language }) {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const t = translations[language];

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const weatherRes = await axios.get(`${API_BASE}/weather?location=Bangalore`);
      setWeatherData(weatherRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">{t.loading}</div>;
  }

  return (
    <div>
      <h2>üè† {t.dashboard}</h2>
      <p style={{ marginBottom: '20px', color: '#666' }}>{t.welcomeMsg}</p>

      <div className="grid">
        <div className="card">
          <h3>üå§Ô∏è Current Weather</h3>
          {weatherData && (
            <>
              <p><strong>Location:</strong> {weatherData.location}</p>
              <p><strong>Temperature:</strong> {weatherData.temperature}¬∞C</p>
              <p><strong>Humidity:</strong> {weatherData.humidity}%</p>
              <p><strong>Condition:</strong> {weatherData.description}</p>
            </>
          )}
        </div>

        <div className="card">
          <h3>üåæ Quick Tips</h3>
          <p>‚Ä¢ Check weather alerts daily</p>
          <p>‚Ä¢ Monitor crop health regularly</p>
          <p>‚Ä¢ Apply fertilizers as recommended</p>
          <p>‚Ä¢ Keep track of market prices</p>
        </div>

        <div className="card">
          <h3>üì± Features</h3>
          <p>‚úÖ Real-time weather updates</p>
          <p>‚úÖ Crop recommendations</p>
          <p>‚úÖ Disease detection</p>
          <p>‚úÖ Market price tracking</p>
          <p>‚úÖ Government schemes info</p>
          <p>‚úÖ Voice assistant support</p>
        </div>
      </div>

      {weatherData && weatherData.alerts && weatherData.alerts.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h3>‚ö†Ô∏è Weather Alerts</h3>
          {weatherData.alerts.map((alert, index) => (
            <div key={index} className={`alert alert-${alert.severity === 'high' ? 'danger' : 'warning'}`}>
              {language === 'hi' ? alert.message_hi : language === 'kn' ? alert.message_kn : alert.message}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
