import React, { useState } from 'react';
import axios from 'axios';
import { translations } from '../translations/translations';

const API_BASE = 'http://localhost:5000';

function CropRecommendation({ language }) {
  const [soilType, setSoilType] = useState('loamy');
  const [season, setSeason] = useState('kharif');
  const [location, setLocation] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [soilAnalysis, setSoilAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const t = translations[language];

  const fetchRecommendations = async () => {
    setLoading(true);
    try {
      const cropRes = await axios.get(`${API_BASE}/crop?soil=${soilType}&season=${season}&location=${location}`);
      setRecommendations(cropRes.data.recommendations);

      const soilRes = await axios.post(`${API_BASE}/soil`, { type: soilType });
      setSoilAnalysis(soilRes.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      alert('Error fetching recommendations. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>üå± {t.cropRec}</h2>

      <div className="form-group">
        <label>{t.soilType}</label>
        <select value={soilType} onChange={(e) => setSoilType(e.target.value)}>
          <option value="clay">Clay (‡§ö‡§ø‡§ï‡§®‡•Ä ‡§Æ‡§ø‡§ü‡•ç‡§ü‡•Ä / ‡≤ú‡≥á‡≤°‡≤ø‡≤Æ‡≤£‡≥ç‡≤£‡≥Å)</option>
          <option value="sandy">Sandy (‡§∞‡•á‡§§‡•Ä‡§≤‡•Ä ‡§Æ‡§ø‡§ü‡•ç‡§ü‡•Ä / ‡≤Æ‡≤∞‡≤≥‡≥Å ‡≤Æ‡≤£‡≥ç‡≤£‡≥Å)</option>
          <option value="loamy">Loamy (‡§¶‡•ã‡§Æ‡§ü ‡§Æ‡§ø‡§ü‡•ç‡§ü‡•Ä / ‡≤≤‡≥ã‡≤Æ‡≤ø ‡≤Æ‡≤£‡≥ç‡≤£‡≥Å)</option>
          <option value="black">Black (‡§ï‡§æ‡§≤‡•Ä ‡§Æ‡§ø‡§ü‡•ç‡§ü‡•Ä / ‡≤ï‡≤™‡≥ç‡≤™‡≥Å ‡≤Æ‡≤£‡≥ç‡≤£‡≥Å)</option>
        </select>
      </div>

      <div className="form-group">
        <label>{t.season}</label>
        <select value={season} onChange={(e) => setSeason(e.target.value)}>
          <option value="kharif">Kharif - Monsoon (‡§ñ‡§∞‡•Ä‡§´ / ‡≤ñ‡≤∞‡≥Ä‡≤´‡≥ç)</option>
          <option value="rabi">Rabi - Winter (‡§∞‡§¨‡•Ä / ‡≤∞‡≤¨‡≤ø)</option>
          <option value="summer">Summer (‡§ó‡•ç‡§∞‡•Ä‡§∑‡•ç‡§Æ / ‡≤¨‡≥á‡≤∏‡≤ø‡≤ó‡≥Ü)</option>
          <option value="all">All Season (‡§∏‡§≠‡•Ä ‡§Æ‡•å‡§∏‡§Æ / ‡≤é‡≤≤‡≥ç‡≤≤‡≤æ ‡≤ã‡≤§‡≥Å‡≤ó‡≤≥‡≥Å)</option>
        </select>
      </div>

      <div className="form-group">
        <label>{t.location} (Optional)</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter your location"
        />
      </div>

      <button className="btn" onClick={fetchRecommendations} disabled={loading}>
        {loading ? t.loading : t.getRecommendations}
      </button>

      {soilAnalysis && (
        <div style={{ marginTop: '20px' }}>
          <h3>üåç Soil Analysis - {soilAnalysis.soil_type.toUpperCase()}</h3>
          <div className="card">
            <p><strong>üíß Water Requirement:</strong> {soilAnalysis.water_requirement}</p>
            <p><strong>üåæ Suitable Crops:</strong> {soilAnalysis.crops.join(', ')}</p>
            <p><strong>üß™ Recommended Fertilizers:</strong> {soilAnalysis.fertilizers.join(', ')}</p>
            <p><strong>üí° Tips:</strong> {soilAnalysis.tips}</p>
          </div>
        </div>
      )}

      {recommendations.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h3>Recommended Crops ({recommendations.length})</h3>
          <div className="grid">
            {recommendations.map((crop, index) => (
              <div key={index} className="card">
                <h3>
                  {language === 'hi' ? crop.name_hi : language === 'kn' ? crop.name_kn : crop.name}
                </h3>
                <p><strong>üå°Ô∏è Ideal Temp:</strong> {crop.ideal_temp}</p>
                <p><strong>üíß Water:</strong> {crop.water_requirement}</p>
                <p><strong>üß™ Fertilizers:</strong> {crop.fertilizers.join(', ')}</p>
                <p><strong>‚è±Ô∏è Duration:</strong> {crop.duration}</p>
                <p><strong>üìä Expected Yield:</strong> {crop.yield}</p>
                <div style={{ marginTop: '10px' }}>
                  {crop.seasons.map((s, i) => (
                    <span key={i} className="badge badge-success">{s}</span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {!loading && recommendations.length === 0 && (
        <div className="alert alert-warning" style={{ marginTop: '20px' }}>
          No recommendations found. Please try different parameters.
        </div>
      )}
    </div>
  );
}

export default CropRecommendation;
